import React from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from '@fortawesome/fontawesome-svg-core'
import { fas } from "@fortawesome/free-solid-svg-icons";
import { far } from "@fortawesome/free-regular-svg-icons";
import { Slider, InputNumber, Upload, message, Button, Switch } from 'antd';
import 'antd/dist/antd.css';
import './sound.scss'
import {selectStatusSound, setStatusSound, uploadVoiceFile ,uploadVoiceEffect, getVoiceMaster, setVoiceMaster} from "../../services/APIs/sound"
import { GetLanguage, SetLanguage } from "../../services/APIs/Setting";

library.add(fas)

let word = {
    'Setting': { 'EN': 'Setting', 'TH': 'เสร็จแล้ว' },
    'Sound': { 'EN': 'Sound', 'TH': 'เสร็จแล้ว' },
    'Master Volume': { 'EN': 'Master Volume', 'TH': 'เสร็จแล้ว' },
    'Thai': { 'EN': 'Thai', 'TH': 'เสร็จแล้ว' },
    'Voice': { 'EN': 'Voice', 'TH': 'เสร็จแล้ว' },
    'Sound Effect': { 'EN': 'Sound Effect', 'TH': 'เสร็จแล้ว' },
    'Upload': { 'EN': 'Upload', 'TH': 'เสร็จแล้ว' },
    'Voice feedback': { 'EN': 'Voice feedback', 'TH': 'เสร็จแล้ว' },


}

class Sound extends React.Component {

    constructor(props) {

        super(props)
        this.state = {
            language: 'TH',
            inputValue: 0,
            loading: false,
            loading_page: false,
            statusSound: true,
            soundFile:undefined,
            soundEffect:undefined
        }

    }

    componentDidMount() {
        this.setState({
            loading_page: true
        })
        // get data from mongodb
        selectStatusSound()
        .then(_Sound => {
            // console.log("_Sound.data.msg:",_Sound.data.msg[0].statusSound)
            if (_Sound.data.status) {

                setTimeout(() => {
                    this.setState({
                        loading_page: false
                    })
                }, 800);
                this.setState({
                    statusSound: _Sound.data.msg[0].statusSound,
                })

            }
        })
        .catch(_DisplayError => {
            window.location.href = "/password";
        })
        // inputValue
        getVoiceMaster()
        .then(_SoundMaster => {
            // console.log("_SoundMaster.data.msg[0]:",_SoundMaster.data.msg)
            // console.log(_SoundMaster.data);
            if (_SoundMaster.data.status) {

                setTimeout(() => {
                    this.setState({
                        loading_page: false
                    })
                }, 800);
                this.setState({
                    inputValue: _SoundMaster.data.msg,
                })

            }
        })
        .catch(errorVoice => {
            // console.log(errorVoice[0])
            window.location.href = "/password";
        })
        GetLanguage() // Get language for display
        .then(_Edit => {
            if (_Edit.data.status) {
                this.setState({
                    language: _Edit.data.msg[0].lang,
                })
            }
        })

        // print log status sound
        // console.log(this.state.statusSound)
    }

    onChange = value => {
        this.setState({
            inputValue: value,
        });

        setVoiceMaster(value)
        .then(_welcomeWord => {
            if(_welcomeWord.data.status){
                message.success({
                    content: word['Done'][this.state.language],
                    className: 'message-done',
                    style: {
                        marginTop: '2vh',
                    },
                });
            }else{
                message.error({
                    content: word['Please try again.'][this.state.language],
                    className: 'message-alert',
                    style: {
                        marginTop: '2vh',
                    },
                });
            }
        })
        .catch(_StatusFaceError => console.error(_StatusFaceError))
    };


    handleChange_effect_voice = (info) =>{
        if (info.file.status === 'uploading') {
            this.setState({ loading: true });
            return;
        }

        setTimeout(async () => {
            const isLt2M = info.file.size / 1024 / 1024 < 2;
            if (isLt2M && (info.file.type === 'audio/mpeg' || info.file.type === 'audio/x-wav')) {
              info.file.status = 'done'
            } else {
              info.file.status = 'error'
            }
            if (info.file.status === "done") {
              // Get this url from response in real world.
              console.log('img----',info.file.originFileObj)
              this.setState({
                  loading: false,
                  soundEffect:info.file.originFileObj
              })

              const res = await uploadVoiceEffect({
                  soundEffects:this.state.soundEffect
              })
              console.log('res---> ', res)
              if(res.data.status){
                  message.success({
                      content: word['Done'][this.state.language],
                      className: 'message-done',
                      style: {
                          marginTop: '2vh',
                      },
                  });

              }else{
                  message.error({
                      content: word['Please try again.'][this.state.language],
                      className: 'message-alert',
                      style: {
                          marginTop: '2vh',
                      },
                  });
              }
            }
          }, 1000) 
    }


    handleChange_voice = (info) => {
        if (info.file.status === 'uploading') {
            this.setState({ loading: true });
            return;
        }

        setTimeout(async () => {
          const isLt2M = info.file.size / 1024 / 1024 < 2;
          if (isLt2M && (info.file.type === 'audio/mpeg' || info.file.type === 'audio/x-wav')) {
            info.file.status = 'done'
          } else {
            info.file.status = 'error'
          }
          if (info.file.status === "done") {
            // Get this url from response in real world.
            console.log('img----',info.file.originFileObj)
            this.setState({
                loading: false,
                soundFile:info.file.originFileObj
            })

            const res = await uploadVoiceFile({
                soundVoice:this.state.soundFile
            })
            console.log('res---> ', res)
            if(res.data.status){
                message.success({
                    content: word['Done'][this.state.language],
                    className: 'message-done',
                    style: {
                        marginTop: '2vh',
                    },
                });

            }else{
                message.error({
                    content: word['Please try again.'][this.state.language],
                    className: 'message-alert',
                    style: {
                        marginTop: '2vh',
                    },
                });
            }
          }
        }, 1000)    
    }


    onChangevoice(checked) {
        // console.log(`switch to ${checked}`);
        this.setState({
            statusSound:checked
        })

        setStatusSound(checked)
        .then(_statusSound => {
            if(_statusSound.data.status){
                message.success({
                    content: word['Done'][this.state.language],
                    className: 'message-done',
                    style: {
                        marginTop: '2vh',
                    },
                });
            }else{
                message.error({
                    content: word['Please try again.'][this.state.language],
                    className: 'message-alert',
                    style: {
                        marginTop: '2vh',
                    },
                });
            }
        })
        .catch(_StatusFaceError => console.error(_StatusFaceError))
    }


    render() {

        // console.log(this.state.statusSound)
        console.log(this.state.inputValue);

        const { inputValue } = this.state;



        const props_voice = {
            name: 'file',
            headers: {
                authorization: 'authorization-text',
            },
            onChange: this.handleChange_voice,

        };

        const props_effect = {
            name: 'file',
            headers: {
                authorization: 'authorization-text',
            },
            onChange: this.handleChange_effect_voice,
        };

