import React from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from '@fortawesome/fontawesome-svg-core'
import { fas } from "@fortawesome/free-solid-svg-icons";
import { far } from "@fortawesome/free-regular-svg-icons";
import { GetCheckin, setTimeCheckIn, setOpenCheckIn, setMon, setTue, setWed, setThu, setFri, setSat, setSun } from "../../services/APIs/Setting";
import moment from 'moment';

import { Switch, TimePicker, Checkbox, message } from 'antd';
import { RotateSpinner } from "react-spinners-kit";

import 'antd/dist/antd.css';
import './checkin.scss'
import { GetLanguage, SetLanguage } from "../../services/APIs/Setting";

library.add(fas)


const format = 'HH:mm';

let word = {
    'Done': { 'EN': 'Done', 'TH': 'เสร็จแล้ว' },
    'Please try again.': { 'EN': 'Please try again.', 'TH': 'กรุณาลองอีกครั้ง.' },
    'Check in': { 'EN': 'Check in', 'TH': 'เช็คอิน' },
    'On time': { 'EN': 'On time', 'TH': 'ตรงเวลา' },
    'Working day': { 'EN': 'Working day', 'TH': 'วันทำงาน' },
    'Mon': { 'EN': 'Mon', 'TH': 'จันทร์' },
    'Tue': { 'EN': 'Tue', 'TH': 'อังคาร' },
    'Wed': { 'EN': 'Wed', 'TH': 'พุธ' },
    'Thu': { 'EN': 'Thu', 'TH': 'พฤหัสบดี' },
    'Fri': { 'EN': 'Fri', 'TH': 'ศุกร์' },
    'Sat': { 'EN': 'Sat', 'TH': 'เสาร์' },
    'Sun': { 'EN': 'Sun', 'TH': 'อาทิตย์' },

}

class Checkin extends React.Component {

    constructor(props) {

        super(props)
        this.state = {
            language:"TH",
            mon: true,
            tue: true,
            wed: true,
            thu: true,
            fri: true,
            sat: true,
            sun: true,
            time: moment().format(),
            open: true,
            loading: false
        }

    }




    componentDidMount() {
        this.setState({
            loading: true
        })
        GetCheckin() // display
            .then(_Mode => {


                if (_Mode.data.status) {

                    setTimeout(() => {
                        this.setState({
                            loading: false
                        })
                    }, 500);


                    this.setState({
                        mon: _Mode.data.msg[0].workingDayMo,
                        tue: _Mode.data.msg[0].workingDayTu,
                        wed: _Mode.data.msg[0].workingDayWed,
                        thu: _Mode.data.msg[0].workingDayThu,
                        fri: _Mode.data.msg[0].workingDayFri,
                        sat: _Mode.data.msg[0].workingDaySat,
                        sun: _Mode.data.msg[0].workingDaySun,
                        time: _Mode.data.msg[0].ontime,
                        open: _Mode.data.msg[0].checkinStatus
                    })

                }
            })

            .catch(_ModeError => {
                window.location.href = "/password";
            })
        GetLanguage() // Get language for display
            .then(_Edit => {


                if (_Edit.data.status) {

                    setTimeout(() => {
                        this.setState({
                            loading: false
                        })
                    }, 800);
                    this.setState({
                        language: _Edit.data.msg[0].lang,

                    })


                }
            })
    }

    onChangeOpenCheck(checked) { // status open-close checkin
        this.setState({
            open: checked
        })

        setOpenCheckIn(checked)
            .then(_StatusOpen => {
                if (_StatusOpen.data.status) {

                    message.success({
                        content: word['Done'][this.state.language],
                        className: 'message-done',
                        style: {
                            marginTop: '2vh',
                        },
                    });

                } else {
                    message.error({
                        content: word['Please try again.'][this.state.language],
                        className: 'message-alert',
                        style: {
                            marginTop: '2vh',
                        },
